    /* Injected CSS Code */
    /* Base Styles */
body {
    font-family: 'Inter', sans-serif;
    line-height: 1.6;
    color: #333;
    background-color:rgba(245, 247, 250, 0.61);
    margin: 0;
    padding: 0;
}

/* Form Container */
.form-all {
    max-width: 800px;
    width: 90%;
    margin: 2rem auto;
    padding: 2rem;
    background: white;
    border-radius: 12px;
    box-shadow: 0 4px 20px rgba(0, 0, 0, 0.08);
}

/* Header Styles */
.form-header-group {
    margin-bottom: 2rem;
    text-align: center;
}

.form-header {
    color: #2c3e50;
    font-size: 2rem;
    font-weight: 700;
    margin-bottom: 0.5rem;
}

.form-subHeader {
    color: #7f8c8d;
    font-size: 1.1rem;
}

/* Form Line Items */
.form-line {
    margin-bottom: 1.5rem;
    padding: 1.25rem;
    background: #f8fafc;
    border-radius: 8px;
    transition: all 0.3s ease;
}

.form-line:hover {
    background: #f1f5f9;
    box-shadow: 0 2px 8px rgba(0, 0, 0, 0.05);
}

/* Labels */
.form-label-top {
    display: block;
    font-weight: 600;
    color: #2c3e50;
    margin-bottom: 0.75rem;
    font-size: 1rem;
}

/* Input Fields */
.form-textbox, 
.form-textarea, 
.form-dropdown,
input[type="date"],
input[type="tel"],
input[type="email"] {
    width: 100%;
    padding: 0.75rem 1rem;
    border: 1px solid #e2e8f0;
    border-radius: 6px;
    font-size: 1rem;
    transition: all 0.3s ease;
    box-sizing: border-box;
    background: white;
}

.form-textbox:focus, 
.form-textarea:focus, 
.form-dropdown:focus,
input[type="date"]:focus,
input[type="tel"]:focus,
input[type="email"]:focus {
    border-color: #3b82f6;
    outline: none;
    box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.15);
}

/* Radio Buttons */
.form-radio-item {
    margin-bottom: 0.5rem;
    display: flex;
    align-items: center;
}

.form-radio {
    margin-right: 0.75rem;
}

/* Buttons */
.form-submit-button {
    background-color: #3b82f6;
    color: white;
    border: none;
    padding: 0.75rem 1.5rem;
    font-size: 1rem;
    border-radius: 6px;
    cursor: pointer;
    transition: all 0.3s ease;
    font-weight: 600;
    margin-right: 1rem;
}

.form-submit-button:hover {
    background-color: #2563eb;
    transform: translateY(-1px);
}

/* Search Button Specific */
button[name="Search"] {
    background-color: #10b981;
}

button[name="Search"]:hover {
    background-color: #059669;
}

/* Error Messages */
.error {
    color: #ef4444;
    font-size: 0.875rem;
    margin-top: 0.5rem;
    display: block;
}

/* Button Wrapper */
.form-buttons-wrapper {
    display: flex;
    flex-wrap: wrap;
    gap: 1rem;
}

/* Responsive Adjustments */
@media (max-width: 768px) {
    .form-all {
        padding: 1.5rem;
        width: 95%;
    }
    
    .form-header {
        font-size: 1.75rem;
    }
    
    .form-subHeader {
        font-size: 1rem;
    }
    
    .form-line {
        padding: 1rem;
    }
    
    .form-buttons-wrapper {
        flex-direction: column;
    }
    
    .form-submit-button {
        width: 100%;
        margin-bottom: 0.5rem;
        margin-right: 0;
    }
}

@media (max-width: 480px) {
    .form-all {
        padding: 1rem;
    }
    
    .form-header {
        font-size: 1.5rem;
    }
    
    input, select, textarea {
        font-size: 0.9rem;
    }
}

/* Accessibility Improvements */
input:focus, 
button:focus, 
select:focus {
    outline: 2px solid #3b82f6;
    outline-offset: 2px;
}

/* Remove default button styling inside anchor */
button a {
    color: inherit;
    text-decoration: none;
    display: block;
    height: 100%;
}
