    $FullnameErr = $DobErr = $NicErr = $AddressErr = $PhoneErr = $EmailErr = $OccupationErr = $GenderErr = $RegisteredDateErr = "";
    $fullname = $dateofbirth = $nic = $address = $phonenumber = $email = $occupation = $gender = $registereddate = "";

    if($_SERVER["REQUEST_METHOD"] == "POST")
    {
        if(empty($_POST["full_name"]))
        {
            $FullnameErr = "Full Name Required";
        }
        else
        {
            $fullname = test_input($_POST["full_name"]);
            if(!preg_match("/^[a-zA-Z-' ]*$/", $fullname))
            {
                $FullnameErr = "Only letters and white space allowed";
            }
            $_POST["full_name"] = $fullname;
        }
        if(empty($_POST["dob"]))
        {
            $DobErr = "Date of Birth Required";
        }
        else
        {
            $dateofbirth = test_input($_POST["dob"]);
            $_POST["dob"] = $dateofbirth;
        }
        if(empty($_POST["nic"]))
        {
            $NicErr = "NIC Required";
        }
        else
        {
            $nic = test_input($_POST["nic"]);
            if(!preg_match("/^[0-9]{12}$/", $nic))
            {
                $NicErr = "Invalid NIC Format";
            }
            $_POST["nic"] = $nic;
        }
        if(empty($_POST["address"]))
        {
            $AddressErr = "Address Required";
        }
        else
        {
            $address = test_input($_POST["address"]);
            $_POST["address"] = $address;
        }
        if(empty($_POST["phone"]))
        {
            $PhoneErr = "Phone Number Required";
        }
        else
        {
            $phonenumber = test_input($_POST["phone"]);
            if(!preg_match("/^[0-9]{10}$/", $phonenumber))
            {
                $PhoneErr = "Invalid Phone Number Format";
            }
            $_POST["phone"] = $phonenumber;
        }
         // Validate email        
        if(empty($_POST["email"]))
        {
            $EmailErr = "Email Required";
        }
        else
        {
            $email = test_input($_POST["email"]);
            if(!filter_var($email, FILTER_VALIDATE_EMAIL))
            {
                $EmailErr = "Invalid Email Format";
            }
            $_POST["email"] = $email;
        }
        // Validate occupation
        if(empty($_POST["occupation"]))
        {
            $OccupationErr = "Occupation Required";
        }
        else
        {
            $occupation = test_input($_POST["occupation"]);
            if(!preg_match("/^[a-zA-Z-' ]*$/", $occupation))
            {
                $OccupationErr = "Only letters and white space allowed";
            }
            $_POST["occupation"] = $occupation;
        }
        if(empty($_POST["gender"]))
        {
            $GenderErr = "Select the gender";
        }
        else
        {
            $gender = test_input($_POST["gender"]);
            $_POST["gender"] = $gender;
        }
        if(empty($_POST["registered_date"]))
        {
            $RegisteredDateErr = "Registered Date Required";
        }
        else
        {
            $registereddate = test_input($_POST["registered_date"]);
            $_POST["registered_date"] = $registereddate;
        }
         // Redirect to the output page if there are no errors
    
    if(empty($FullnameErr) && empty($EmailErr) && empty($DobErr) && empty($NicErr) && empty($AddressErr) && empty($PhoneErr) && empty($OccupationErr) && empty($GenderErr) && empty($RegisteredDateErr))
         {
            include ("form_validation_process.php");
             exit();
         }
      }
    function test_input($data)
       {
        $data = trim($data);
        $data = stripslashes($data);
        $data = htmlspecialchars($data);
        return $data;
     }
